<?php
use yii\helpers\HtmlPurifier;
use yii\widgets\ListView;

?>


<div class="media">
    <div class="media-left">
        <a href="#">
            <img class="media-object" src="data:image/svg+xml;base64," alt="...">
        </a>
    </div>
    <div class="media-body">
        <h4 class="media-heading"><?=date('Y-m-d H:i:s', $model->create_at)?></h4>
        <?php echo HtmlPurifier::process($model->text) ?>
        <?php $comments = \common\service\ContentService::factory()->getComments($model->content_id, $model->id)?>
        <?php if (!empty($comments)) { ?>
            <?php echo ListView::widget(array(
                'summary' => '',
                'itemOptions' => array('class'=>'media'),
                'dataProvider' => $comments,
                'itemView' => '_comment_list',
                'emptyText' => '',
            ))?>
        <?php } ?>
    </div>
</div>
